mutation ProjectManagementCreateProject($name: String!, 
    $dueDate: DateTime!, 
    $startDate: DateTime,
    $account: ProjectManagement_CompanyInput!, 
    $assignee: ProjectManagement_PersonaInput
    $description: String,
    $type: String,
    $priority: Int,
    $completionRate: Decimal,
    $pinned: Boolean,
    $emailAddress: [Qb_EmailAddressInput],
    $addresses: [Qb_PostalAddressInput],
    $status: ProjectManagement_Status,
) {
  projectManagementCreateProject(input:{
    name: $name,
    dueDate : $dueDate,
    startDate : $startDate,
    account: $account,
    assignee: $assignee,
    description: $description,
    type: $type,
    priority: $priority,
    completionRate: $completionRate,
    pinned: $pinned,
    emailAddress: $emailAddress,
    addresses: $addresses,
    status: $status
  }
    )
    {
           ... on ProjectManagement_Project {
        name, 
        dueDate,
        id,
        completionRate,
        account {
            id
           }
        emailAddress {
            email
        }
        addresses {
            streetAddressLine1,
            postalCode,
            city
        }
        }
    }
}

